            password,
            name: emailList[i].sender_name,
            recipient_name: emailList[i].receiver_name,
          },
        })
        .then(() => {
          //success
          setLoading(false);
          setSuccess(true);
        })
        .catch(() => {
          setError("Error in sending Email, Please contact Developer");
          setLoading(false);
          handleClick();
        });
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Receipient Email"
        onChange={(e) => setEmail(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Subject"
        onChange={(e) => setSubject(e.target.value)}
      />
      <br />
      <textarea placeholder="Message" onChange={(e) => e.target.value} />
      <br />
      <button>Send Email</button>
    </div>
  );
}

export default App;
