import { useState } from "react";

function App() {
  const [email, setEmail] = useState();
  const [subject, setSubject] = useState();
  const [message, setMessage] = useState();

  const sendMail = () => {
    axios
      .get("http://localhost:5000/", {
        params: {
          email,
          subject,
          message,
        },
      })
      .then(() => {
        //success
       alert("Succ")
      })
      .catch(() => {
        setError("Error in sending Email, Please contact Developer");
        setLoading(false);
        handleClick();
      });
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Receipient Email"
        onChange={(e) => setEmail(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Subject"
        onChange={(e) => setSubject(e.target.value)}
      />
      <br />
      <textarea placeholder="Message" onChange={(e) => e.target.value} />
      <br />
      <button>Send Email</button>
    </div>
  );
}

export default App;
